import React, { useEffect, useRef, useState } from 'react';

const idleVideos = [
  '/assets/idle/idle1.mp4',
  '/assets/idle/idle2.mp4',
  '/assets/idle/idle3.mp4',
  '/assets/idle/idle4.mp4'
];

export default function PlayerWindow() {
  const videoRef = useRef(null);
  const [src, setSrc] = useState(idleVideos[Math.floor(Math.random() * idleVideos.length)]);

  useEffect(() => {
    let mounted = true;

    function pickNext() {
      if (!mounted) return;
      const next = idleVideos[Math.floor(Math.random() * idleVideos.length)];
      setSrc(next);
    }

    const interval = setInterval(() => {
      pickNext();
    }, 7000); // change idle every 7s

    return () => { mounted = false; clearInterval(interval); };
  }, []);

  return (
    <div style={{height:'100vh',display:'flex',alignItems:'center',justifyContent:'center',background:'#000'}}>
      <video src={src} autoPlay loop muted style={{width:'100%',height:'100%',objectFit:'cover'}} ref={videoRef} />
    </div>
  );
}
